DROP TABLE IF EXISTS `wp_links`;
CREATE TABLE `wp_links` (
  `link_id` bigint(20) unsigned NOT NULL AUTO_INCREMENT COMMENT '每个链接的唯一ID号',
  `link_url` varchar(255) NOT NULL DEFAULT '' COMMENT '每个链接的URL地址',
  `link_name` varchar(255) NOT NULL DEFAULT '' COMMENT '单个链接的名字',
  `link_image` varchar(255) NOT NULL DEFAULT '' COMMENT ' 链接可以被定义为使用图片链接',
  `link_target` varchar(25) NOT NULL DEFAULT '' COMMENT '链接打开的方式，有三种，_blank为以新窗口打开，_top为就在本窗口中打开并在最上一级，none为不选择，会在本窗口中打开。',
  `link_description` varchar(255) NOT NULL DEFAULT '' COMMENT '链接的说明文字。',
  `link_visible` varchar(20) NOT NULL DEFAULT 'Y' COMMENT '该链接是否可以，枚举enum(’Y’,’N’)值，默认为Y，即可见。',
  `link_owner` bigint(20) unsigned NOT NULL DEFAULT '1' COMMENT '某个链接的创建人',
  `link_rating` int(11) NOT NULL DEFAULT '0' COMMENT ' 链接的等级',
  `link_updated` datetime NOT NULL DEFAULT '0000-00-00 00:00:00' COMMENT ' 链接被定义、修改的时间，datetime值。',
  `link_rel` varchar(255) NOT NULL DEFAULT '' COMMENT '链接与定义者的关系，由XFN Creator设置',
  `link_notes` mediumtext NOT NULL COMMENT '链接的详细说明',
  `link_rss` varchar(255) NOT NULL DEFAULT '' COMMENT '该链接的RSS地址',
  PRIMARY KEY (`link_id`),
  KEY `link_visible` (`link_visible`)
) ENGINE=InnoDB DEFAULT CHARSET=utf8;

