  `post_type` varchar(20) NOT NULL DEFAULT 'post' COMMENT '文章类型（post/page等）',
  `post_mime_type` varchar(100) NOT NULL DEFAULT '' COMMENT 'MIME类型',
  `comment_count` bigint(20) NOT NULL DEFAULT '0' COMMENT '评论总数',
